import SearchBar from "./SearchBWF/SearchBar";
import { useState, useEffect, useMemo } from "react";
import FilterT from "./SearchBWF/FilterT";
import Filterl from "./SearchBWF/FilterL";
import { useQuery } from "@tanstack/react-query";
import axios from "axios";
import Repo from "./SearchBWF/Repo";
import { useLocation, useParams } from "react-router-dom";
import ProfileSideBar from "./ProfileSideBar";

export type RepoData = {                     //DEFINING THE TYPE OF THE DATA
  name: string;
  language: string;
  description: string;
  html_url: string;
  created_at: string;
  updated_at: string;
  pushed_at: string;
  stargazers_count: number;
  watchers_count: number;
  forks_count: number;
  open_issues_count: number;
};

const RepoResPage = () => {                   //EXTRACTING THE SEARCH PARAMS FROM THE URL
  function useQue() {
    const { search } = useLocation();         

    return useMemo(() => new URLSearchParams(search), [search]);  //USING THE MEMO HOOK TO STORE THE SEARCH PARAMS
  }

  const [searchText, setSearchText] = useState<string>("");  //SETTING THE STATE FOR THE SEARCH TEXT
  const params = useQue();                                    

  const user = String(params.get("user"));                                  //EXTRACTING THE USER PARAMS FOR THE ProfileSideBar COMPONENT

  const fetchRepoData = async () => {                               //FOR THE USAGE OF REACT QUERY TO FETCH THE REPO DATA
    const response = await axios.get(
      `https://api.github.com/users/${params.get("user")}/repos`  
    );
    return response.data;
  };
  const { data, isLoading, isError } = useQuery(["repoData"], fetchRepoData);   //USING THE REACT QUERY TO FETCH REPO DATA
 

  return (
    <>
    <div className="container items-end w-full px-8 mx-auto max-w-7xl md:px-16 ">  
      {isLoading && (
        <div className="flex items-center justify-center w-full h-96">
          <h1 className="text-2xl font-semibold text-blue-600">Loading...</h1>
        </div>
      )}
      {!isLoading && isError && (
        <div className="flex items-center justify-center w-full h-96">
          <h1 className="text-2xl font-semibold text-red-600">
            No github user found...
          </h1>
        </div>
      )}
      {!isLoading && !isError && data?.length === 0 && (
        <div className="flex items-center justify-center w-full h-96">
          <h1 className="text-2xl font-semibold text-red-600">
            No repositories found...
          </h1>
        </div>
      )}
      {!isLoading && !isError && (
        <>
          <div className="flex items-center justify-center border-b w-full gap-4 md:gap-6"> {/*PASSING THE SEARCH TEXT TO THE SEARCH BAR */}
            <SearchBar searchText={searchText} setSearchText={setSearchText} /> 
            <div className="flex w-full gap-1 md:gap-3">
              <FilterT />
              <Filterl />
            </div>
          </div>
          {data                               //MAPPING THE DATA TO THE REPO COMPONENT
            .filter((repo: RepoData) => {
              return repo.name.toLowerCase().includes(searchText.toLowerCase());
            })
            .map((repo: RepoData, index: number) => (
              <Repo key={index} repo={repo} />
            ))}
        </>
      )}
    </div>
    <ProfileSideBar user={user} />   {/*PASSING THE USER PARAMS TO THE ProfileSideBar COMPONENT*/}
  </>
  );
};

export default RepoResPage;
