import axios from "axios";
import { useQuery } from "@tanstack/react-query";

type puser = {
    user: string;
}

export type profileData = {
    avatar: string;
    real_name: string;
    username: string;
    bio: string;
    followers: number;
    following: number;
    location: string;
    email: string;
    organization: string;
};

const ProfileSideBar = ( user: puser ) => {


    const fetchProfileData = async () => {                                              //FOR THE USAGE OF REACT QUERY TO FETCH THE PROFILE DATA         
        const response = await axios.get(
          `https://api.github.com/users/${user.user}`
        );
        return response.data;
      }
    const { data, isLoading, isError} = useQuery(["profileData"], fetchProfileData);   //USING THE REACT QUERY TO FETCH PROFILE DATA

    return (
        <div className="">
            <img  
            alt="AVATAR" src={data?.avatar_url} 
            width="260" 
            height="260" 
            className="rounded-full width-full border border-gray-200 " 
            />
            <div>
                <h1 className="text-3xl font-bold ">{data?.name}</h1>
                <p className="text-xl text-gray-500">{data?.login}</p>
                <h2 className="text-base font-normal">{data?.bio}</h2>
            </div>
            <button className="px-2 py-1 border rounded w-60 font-semibold" >Follow</button>
            <div className="flex justify-center gap-1">
            <svg 
             xmlns="http://www.w3.org/2000/svg" 
             fill="none" 
             viewBox="0 0 24 24" 
             stroke-width="1.5" 
             stroke="currentColor" 
            className="w-6 h-6">
                <path stroke-linecap="round" stroke-linejoin="round" d="M15 19.128a9.38 9.38 0 002.625.372 9.337 9.337 0 004.121-.952 4.125 4.125 0 00-7.533-2.493M15 19.128v-.003c0-1.113-.285-2.16-.786-3.07M15 19.128v.106A12.318 12.318 0 018.624 21c-2.331 0-4.512-.645-6.374-1.766l-.001-.109a6.375 6.375 0 0111.964-3.07M12 6.375a3.375 3.375 0 11-6.75 0 3.375 3.375 0 016.75 0zm8.25 2.25a2.625 2.625 0 11-5.25 0 2.625 2.625 0 015.25 0z" />
            </svg>

                <span>{data?.followers} followers -</span>
                <span>{data?.following} following </span>
            </div>

        </div>
    )
}

export default ProfileSideBar;
