import SearchBar from "./SearchBWF/SearchBar";
import { useState, useEffect } from 'react';
import FilterT from "./SearchBWF/FilterT";
import Filterl from "./SearchBWF/FilterL";
import octokit from "../Githubclient/GithubC";



const RepoResPage = () => {
  
const [Gdata, setGdata] = useState();

  useEffect(() => {
    fetch("https://api.github.com/users/MHZ0")
    .then((res) => setGdata(res.json()))
    .then((data)=>{console.log(data);},
    (error) => {
      console.log(error);}
      );
      
  },
  
  []);


  return (

    <div className="flex flex-col items-end justify-end ml-auto" style={{ width: '72vw' }}>
      <div className="flex items-center justify-center">

        <SearchBar />
        <FilterT />
        <Filterl />

      </div>
      {}
      <div className="border-y border-gray-300 mx-2 mb-2 w-full">
        <img src={Gdata.avatar_url} alt="Avatar" className="w-12 h-12 rounded-full inline-block mx-2 my-2" />
      </div>
    </div>
  );
};

export default RepoResPage;









