import axios from "axios";
import { useQuery } from "@tanstack/react-query";
import { useLocation } from "react-router-dom";
import { useMemo } from "react";
import  octokit from "../Githubclient/GithubC";
import { useState, useEffect} from "react";

type reponame = {
  name: string;
};

type orgavatar = {
  avatar_url: string;
};



const ProfileSideBar = () => {
  function useQue() {
    const { search } = useLocation();

    return useMemo(() => new URLSearchParams(search), [search]); //USING THE MEMO HOOK TO STORE THE SEARCH PARAMS
  }

  const params = useQue();
  const user = params.get("user") || "mhz0"; //EXTRACTING THE USER PARAMS FOR THE ProfileSideBar COMPONENT
  const fetchProfileData = async () => {
    //FOR THE USAGE OF REACT QUERY TO FETCH THE PROFILE DATA
    const response = await axios.get(`https://api.github.com/users/${user}`);
    return response.data;
  };

  const fetchOrganizations = async () => {
    //FOR THE USAGE OF REACT QUERY TO FETCH THE ORGANIZATION DATA
    const response = await axios.get(
      `https://api.github.com/users/${user}/orgs`
    );
    return response.data;
  };

  const fetchStarred = async () => {
    //FOR THE USAGE OF REACT QUERY TO FETCH THE ORGANIZATION DATA
    const response = await axios.get(
      `https://api.github.com/users/${user}/starred`
    );
    return response.data;
  };

  const { data, isLoading, isError } = useQuery(
    ["profileData"],
    fetchProfileData
  ); //USING THE REACT QUERY TO FETCH PROFILE DATA

  const {
    data: orgData,
    isLoading: orgLoading,
    isError: orgError,
  } = useQuery(["orgData"], fetchOrganizations); //USING THE REACT QUERY TO FETCH ORGANIZATION DATA

  const {
    data: starredData,
    isLoading: starredLoading,
    isError: starredError,
  } = useQuery(["starredData"], fetchStarred); //USING THE REACT QUERY TO FETCH STARRED DATA



  //////

  const fetchRepos = async () => {
  const result = await octokit.request("GET /repos/{owner}/", {
    owner: "MHZ0",
  });
  return result.data ;  
}
 //////

const [repoNames, setRepoNames] = useState([]); // State to hold repository names

 useEffect(() => {
  // Function to fetch repository data and update the state
  const fetchRepoData = async () => {
    try {
      const repoData = await fetchRepos(); // Wait for the fetchRepos promise to resolve
      setRepoNames(repoData.map((repo: any) => repo.name)); // Update the state with repository names
    } catch (error) {
      console.error(`Error fetching repos: ${error}`);
    }
  };

  fetchRepoData(); // Call the fetchRepoData function when the component mounts
}, []); // Empty dependency array means this effect runs once when the component mounts






  return (
    <div className="container w-full max-w-xl p-12 md:w-1/3">
     <div className="flex justify-center">
      <img
        alt="AVATAR"
        src={data?.avatar_url}
        className="w-52 h-52 mx-auto border border-gray-200 rounded-full md:mx-0 width-full "
      />
     </div>
      <div className="flex flex-col items-center w-full md:items-start">
        <h1 className="flex justify-start my-2 text-3xl font-bold ">
          {data?.name}
        </h1>
        <h2 className="flex justify-start mb-5 text-xl text-gray-500">
          {" "}
          {data?.login}
        </h2>
        <p className="max-w-md font-normal text-left ">{data?.bio}</p>
          <button className="px-2 py-1 my-5 mx-auto font-semibold border rounded w-60">
            Follow
          </button>
      </div>

      <div className="flex justify-start gap-1 my-3">
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          stroke-width="1.5"
          stroke="currentColor"
          className="w-6 h-6"
        >
          <path
            stroke-linecap="round"
            stroke-linejoin="round"
            d="M15 19.128a9.38 9.38 0 002.625.372 9.337 9.337 0 004.121-.952 4.125 4.125 0 00-7.533-2.493M15 19.128v-.003c0-1.113-.285-2.16-.786-3.07M15 19.128v.106A12.318 12.318 0 018.624 21c-2.331 0-4.512-.645-6.374-1.766l-.001-.109a6.375 6.375 0 0111.964-3.07M12 6.375a3.375 3.375 0 11-6.75 0 3.375 3.375 0 016.75 0zm8.25 2.25a2.625 2.625 0 11-5.25 0 2.625 2.625 0 015.25 0z"
          />
        </svg>
        <span>{data?.followers} followers -</span>
        <span>{data?.following} following </span>
      </div>
      <p>{repoNames?.map((repo: r ) => repo.name)}</p>
      <div className="border-t ">
        <h1 className="flex justify-start my-4 text-xl font-semibold ">
          Organizations
        </h1>
        <div className="flex justify-start gap-1">
          {orgData?.map((org: orgavatar, index: number) => (
            <img
              key={index}
              alt="ORG"
              src={org.avatar_url}
              width="30"
              height="30"
              className="border border-gray-200 rounded-full width-full "
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProfileSideBar;
