import octokit from "../Githubclient/GithubC";
import axios from "axios";
import { useQuery } from "@tanstack/react-query";

export type profileData = {
    avatar: string;
    real_name: string;
    username: string;
    bio: string;
    followers: number;
    following: number;
    location: string;
    email: string;
    organization: string;
};

const ProfileSideBar = (profile: profileData, user: string ) => {

    const puser = user;

    const fetchProfileData = async () => {                            //FOR THE USAGE OF REACT QUERY TO FETCH THE PROFILE DATA         
        const response = await axios.get(
          `https://api.github.com/users/${puser}`
        );
        return response.data;
      }
    const { data, isLoading, isError} = useQuery(["profileData"], fetchProfileData);   //USING THE REACT QUERY TO FETCH PROFILE DATA

    
    return (
        <div>

<img    alt="AVATAR" src="https://avatars.githubusercontent.com/u/116764870?v=4" 
        width="260" 
        height="260" 
        className="rounded-full width-full border border-gray-200 " />

        </div>
    )
}

export default ProfileSideBar;
